'use strict';

angular.module('iprognos')

.controller('ExampleCtrl', ['auth', '$scope', 'Fabric', 'FabricConstants', 'Keypress', 'store', '$location', function(auth, $scope, Fabric, FabricConstants, Keypress, store, $location) {

	$scope.fabric = {};
	$scope.FabricConstants = FabricConstants;
	$scope.auth = auth;

	var theImg = "data:image/png;base64,"

/**
	var imgElement = document.getElementById('my-image');
	var imgInstance = new fabric.Image(imgElement, {
	  left: 100,
	  top: 100,
	  angle: 30,
	  opacity: 0.85
	});
*/	
	//
	// Creating Canvas Objects
	// ================================================================
	$scope.addShape = function(path) {
		$scope.fabric.addShape('http://fabricjs.com/assets/15.svg');
	};

	$scope.addImage = function(image) {

		$scope.fabric.addImage(theImg);
	};

	$scope.addImageUpload = function(data) {
		var obj = angular.fromJson(data);
		$scope.addImage(obj.filename);
	};

	//
	// Editing Canvas Size
	// ================================================================
	$scope.selectCanvas = function() {
		$scope.canvasCopy = {
			width: $scope.fabric.canvasOriginalWidth,
			height: $scope.fabric.canvasOriginalHeight
		};
	};

	$scope.setCanvasSize = function() {
		$scope.fabric.setCanvasSize($scope.canvasCopy.width, $scope.canvasCopy.height);
		$scope.fabric.setDirty(true);
		delete $scope.canvasCopy;
	};

	//
	// Init
	// ================================================================
	$scope.init = function() {
		$scope.fabric = new Fabric({
			JSONExportProperties: FabricConstants.JSONExportProperties,
			textDefaults: FabricConstants.textDefaults,
			shapeDefaults: FabricConstants.shapeDefaults,
			json: {}
		});
	};

	$scope.$on('canvas:created', $scope.init);

	Keypress.onSave(function() {
		$scope.updatePage();
	});

	$scope.logout = function() {
		auth.signout();
		store.remove('profile');
		store.remove('token');
		$location.path('/login');
	}
}]);
